mapper.select:
select code, param_values value
from cache_init_param
where code like #{code}

select d.dimension_type_code tid,n.dimension_code id, d.channel_type,n.is_all,d.querysql, n.channel_type_name text,d.dimension_type_name tname from dw_newquery_dimension_name n left join (select r.dimension_type_code,t.dimension_type_name,t.channel_type,t.querysql from dw_newquery_sub_dimension_rel r, dw_newquery_dimension_type t where 1=1 and t.type='1'
and subject_code=#{markType}
and r.dimension_type_code=t.dimension_type_code)d on n.dimension_type_code=d.dimension_type_code where n.type='1' order by to_number(n.ord)

select t.title_name from dw_newquery_subject_title t where 1=1
and t.subject_code=#{markType}
order by to_number(title_id)

select sum(nvl(data.kpi_value, 0)) dr, sum(nvl(data.M_TM_VALUE,0)) bylj, sum(nvl(data.D_LD_VALUE, 0)) zr, sum(nvl(data.D_LM_VALUE,0)) sytq, data.kpi_code, data.acct_date from (select k.kpi_code, d.ACCT_DATE, d.PROV_ID, d.AREA_NO,
d.${key},
d.KPI_VALUE, d.M_TM_VALUE, d.D_LD_VALUE, d.D_LM_VALUE from ${table} d, (select r.kpi_code from dw_newquery_module_kpi_rel r left join dw_newquery_kpi k on r.kpi_code=k.kpi_code where 1=1
and r.module_code=#{moduleId} )k where d.KPI_CODE=k.kpi_code)data where 1=1
AND data.prov_id = #{prov}
AND data.area_no = #{city}
AND data.acct_date between
#{minDate}
and
#{date}
and data.${key} in
(${item} in
#{id}
)
group by data.kpi_code,data.acct_date

select sum(nvl(data.kpi_value, 0)) dr, sum(nvl(data.Y_TY_VALUE,0)) bylj, sum(nvl(data.M_LM_VALUE, 0)) zr, sum(nvl(data.M_LY_VALUE,0)) sytq, data.kpi_code, data.acct_date from (select k.kpi_code, d.month_id acct_date, d.PROV_ID, d.AREA_NO,
d.${key},
d.KPI_VALUE, d.Y_TY_VALUE, d.M_LM_VALUE, d.M_LY_VALUE from ${table} d, (select r.kpi_code from dw_newquery_module_kpi_rel r left join dw_newquery_kpi k on r.kpi_code=k.kpi_code where 1=1
and r.module_code=#{moduleId} )k where d.KPI_CODE=k.kpi_code)data where 1=1
AND data.prov_id = #{prov}
AND data.area_no = #{city}
AND data.acct_date between
#{minDate}
and
#{date}
and data.${key} in(
${item} in
#{id}
)
group by data.kpi_code,data.acct_date

select subject_code,icon from dw_newquery_subject where 1=1
and subject_code in
#{id}
